<span class="line"><span style="color:#B392F0">    expect</span><span style="color:#E1E4E8">(getResponse.</span><span style="color:#B392F0">ok</span><span style="color:#E1E4E8">()).</span><span style="color:#B392F0">toBeTruthy</span><span style="color:#E1E4E8">();</span></span>
