<span class="line"><span style="color:#85E89D">      DATABASE_URL</span><span style="color:#E1E4E8">: </span><span style="color:#9ECBFF">mysql://root:\${MYSQL_ROOT_PASSWORD}@db:3306/\${MYSQL_DATABASE}</span></span>
